from PIL import Image

pic = Image.new('RGB',(1024,512))

str = ""
#120,120
x = 100
y = 100
for i in str:
	if i == 'U':#Up
		y = y - 1
		pic.putpixel([x,y],(255, 255, 255))
	elif i == 'D':#Down
		y = y + 1
		pic.putpixel([x,y],(255, 255, 255))
	elif i == 'L':#Left
		x = x - 1
		pic.putpixel([x,y],(255, 255, 255))
	elif i == 'R':#Right
		x = x + 1
		pic.putpixel([x,y],(255, 255, 255))
	else:
		continue
pic.show()
pic.save('result.png')